import React, { Component } from 'react';

export const renderTextareaField = ({
        input,
        label,
        type,
        id,
        className,
        meta: { touched, error, warning }
    }) => (
    <div className="comment-form__row">
        <div className="comment-form__row-inner">
            {touched &&
            ((error && <span className="comment-form__error">{error}</span>) ||
            (warning && <span className="comment-form__warning">{warning}</span>))}
            <label htmlFor={id} className="comment-form__label-text">{label}</label>
            <textarea {...input} id={id} type={type} className="comment-form__textarea" />
        </div>
    </div>
)

export const renderRadioButton = ({
       input,
       caption,
       radioButtonValue,
       handleChange,
       meta: { touched, error }
    }) => (
    <div className="comment-form__radio-wrap">
        <label className="comment-form__radio-label">
            <input
                {...input}
                type="radio"
                className="comment-form__radio"
                value={radioButtonValue}
                checked={radioButtonValue === input.value ? "checked" : false}
            />
            {caption}
            <div className={`comment-form__helper comment-form__helper--` + `${radioButtonValue}`}></div>
        </label>
    </div>
)