import React from 'react'
import { Field, reduxForm } from 'redux-form'

let Form = props => {
    const { handleSubmit } = props
    return (
        <form className={"form " + `${props.formActive ? "form--active" : ""}`} onSubmit={ handleSubmit }>
            <div className="form__header">
                <h2 className="form__title">Регистрация клиента</h2>
                <div
                    onClick={props.togglePopup}
                    className="form__close">
                </div>
            </div>
            <div className="form__main">
                <div className="form__box form__box--left">
                    <div className="form__row">
                        <label htmlFor="firstName" className="form__label-text">Имя</label>
                        <Field name="name" id="firstName" component="input" type="text" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="secondName" className="form__label-text">Фамилия</label>
                        <Field name="secondName" id="secondName" component="input" type="text" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="email" className="form__label-text">Email</label>
                        <Field name="email" id="email" component="input" type="email" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="phone" className="form__label-text">Телефон</label>
                        <Field name="phone" id="phone" component="input" type="text" className="form__input-text"/>
                    </div>
                </div>
                <div className="form__box form__box--right">
                    <div className="form__row">
                        <label htmlFor="region" className="form__label-text">Уезд</label>
                        <Field name="region" id="region" component="input" type="text" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="city" className="form__label-text">Город / Область</label>
                        <Field name="city" id="city" component="input" type="text" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="address" className="form__label-text">Адрес</label>
                        <Field name="address" id="address" component="input" type="text" className="form__input-text"/>
                    </div>
                    <div className="form__row">
                        <label htmlFor="index" className="form__label-text">Индекс</label>
                        <Field name="index" id="index" component="input" type="text" className="form__input-text"/>
                    </div>
                </div>
            </div>
            <div className="form__checkblock">
                <div className="form__checkrow">
                    <Field name="dispatchEmail" id="dispatch-email" component="input" type="checkbox" className="form__input-checkbox"/>
                    <label htmlFor="dispatch-email" className="form__checkbox-ico"></label>
                    <label htmlFor="dispatch-email" className="form__checkbox-label">Рекламная рассылка по э-почте</label>
                </div>
                <div className="form__checkrow">
                    <Field name="dispatchSms" id="dispatch-sms" component="input" type="checkbox" className="form__input-checkbox"/>
                    <label htmlFor="dispatch-sms" className="form__checkbox-ico"></label>
                    <label htmlFor="dispatch-sms" className="form__checkbox-label">Рекламная рассылка по смс</label>
                </div>
            </div>
            <div className="form__btn-wrap">
                <button type="submit" className="form__btn">Зарегистрировать</button>
            </div>
        </form>
    )
}

Form = reduxForm({
    form: 'register'
})(Form)

export default Form;