import React, { Component } from 'react';

export const renderInputField = ({
        input,
        label,
        type,
        id,
        className,
        meta: { touched, error, warning }
    }) => (
    <div className="form__row">
        <div className="form__row-inner">
            {touched &&
            ((error && <span className="form__error">{error}</span>) ||
            (warning && <span className="form__warning">{warning}</span>))}
            <label htmlFor={id} className="form__label-text">{label}</label>
            <input {...input} id={id} type={type} className={className} />
        </div>
    </div>
)

export const renderSelectField = ({
        input,
        label,
        type,
        id,
        className,
        meta: { touched, error },
        children
    }) => (
    <div className="form__row">
        <div className="form__row-inner">
            {touched && error && <span className="form__error">{error}</span>}
            <label htmlFor={id} className="form__label-text">{label}</label>
            <select {...input} id={id} className={className}>
                {children}
            </select>
        </div>
    </div>
)

export const renderCheckboxField = ({
        input,
        label,
        id,
        name,
        meta: { touched, error }
    }) => (
    <div className="form__checkrow">
        <input {...input} name={name} id={id} type="checkbox" className="form__input-checkbox"/>
        <label htmlFor={id} className="form__checkbox-ico"></label>
        <label htmlFor={id} className="form__checkbox-label">{label}</label>
    </div>
)