import React, {Component} from 'react'
import {connect} from 'react-redux'

import {
    getClientById
} from 'selectors'

class Client extends Component {
    renderMain () {
        const { client } = this.props;

        return (
            <div className="personal__main">
                <div className="personal__left">
                    <div className="personal__row">
                        <span className="personal__row-label">Имя</span>
                        <span className="personal__row-value">{client.name}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Фамилия</span>
                        <span className="personal__row-value">{client.secondName}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Email</span>
                        <span className="personal__row-value">{client.email}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Телефон</span>
                        <span className="personal__row-value">{client.phone}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Уезд</span>
                        <span className="personal__row-value">{client.region}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Город / Область</span>
                        <span className="personal__row-value">{client.city}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Адресс</span>
                        <span className="personal__row-value">{client.address}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Индекс</span>
                        <span className="personal__row-value">{client.index}</span>
                    </div>
                </div>
                <div className="personal__right">
                    <div className="personal__row">
                        <span className="personal__row-label">Статус клиента</span>
                        <span className="personal__row-value">Активный</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Источник</span>
                        <span className="personal__row-value">Через сайт</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">IP регистрации</span>
                        <span className="personal__row-value"><span className="personal__value--underline">{client.ipInfo.ip}</span> <span>( {client.ipInfo.country} )</span></span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Язык обращения</span>
                        <span className="personal__row-value">{client.language}</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Пригласил друзей</span>
                        <span className="personal__row-value">0</span>
                    </div>
                    <div className="personal__row">
                        <span className="personal__row-label">Рекламная рассылка</span>
                        <span className="personal__row-value personal__row-value--checkbox">
                            <div>
                                <input type="checkbox" checked={client.dispathEmail} disabled={true}/>
                                <label>э-почта</label>
                            </div>
                            <div>
                                <input type="checkbox" checked={client.dispathSms} disabled={true}/>
                                <label>смс</label>
                            </div>
                        </span>
                    </div>
                </div>
            </div>
        )
    }

    render () {
        const { client } = this.props;

        return (
            <div className="client">
                <div className="personal">
                    <div className="personal__header">
                        <h2 className="personal__title">Данные клиента</h2>
                        <span className="personal_edit">Редактировать</span>
                    </div>
                    {client && this.renderMain()}
                </div>
                <div className="comments"></div>
            </div>
        )
    }
}

const mapStateToProps = (state, ownProps) => ({
    client: getClientById(state, ownProps.params.id)
})

export default connect(mapStateToProps, null)(Client);